import React from 'react';
//import Header from './header2-1' 
//import Content from './content2-1'
//import Total from './total2-1'

const Header = ({course}) => <h1>{course.name}</h1>

const Content = ( {course} ) => {
  console.log(course);
  return (
      <div >
        { course.parts.map( (element)=><p key={element.id}>{element.name} {element.exercises}</p>)}

      </div>
  );
};

const Total = ({course}) => {
  return (
      <div>
          <h3>Total { course.parts.reduce((s,p)=>s+p.exercises,0) } exercise</h3>
      </div>
  );
};


const Course= ({course}) => {
    return ( <div>

          { course.map((element)=>< div >
          <Header   course={element} /> 
          <Content  course={element} /> 
          <Total    course={element} />


          </div>

          )
          }
          </div>   
    );
};

export default Course
