import { useState, useEffect } from 'react'
import axios from 'axios'
//import Weather from './Weather'
import Countryinfo from './Countryinfo'
import Countrylist from './Countrylist'

const App=({countries ,setCountries})=>{

    const[search,setSearch]=useState('')

    const[weather,setWeather]=useState({})





    const filteredCountry=countries.filter(
                     (country)=>country.name.toUpperCase().includes(search.toUpperCase()) ) 

    const capital=filteredCountry.map((el)=>el.capital)

    console.log(search,capital);

    //filteredCountry.length===1&&filteredCountry.map((el)=>el.capital)

    useEffect(()=> {
           if( capital!==undefined && capital.length===1  ){
           axios.get( `http://api.weatherapi.com/v1/current.json?key=${process.env.REACT_APP_API_KEY}&q=${capital[0]}`)
            .then(response=>console.log(response.data))
     }      },[search]) 

    const handleSearch=(e)=> setSearch(e.target.value)



    const handleShow =(e)=>setSearch(e.target.value)

    return(<div>
             find countries:<input type='text' value={search} onChange={handleSearch}/>

            {
              ( filteredCountry.length>10 ? 
                <p>Too many matches,specify another filter</p>:
                ( filteredCountry.length===1  ? 
                 <div>
                 <Countryinfo filteredCountry={filteredCountry} />
                {/*  <Weather weather={weather}/> */}  
                 </div> :
                 <Countrylist filteredCountry={filteredCountry} handleShow={handleShow}/>
                 )
              )
            }
          </div>)
}

export default App
