import { useState, useEffect } from 'react'
import axios from 'axios'

const App=()=>{

    const[countries,setCountries]=useState([])

    const[searchcountry,setSearch]=useState('')

    const[weather,setWeather]=useState({})

    const filteredCountry=countries.filter(
                     (country)=>country.name.toUpperCase().includes(searchcountry.toUpperCase()) ) 

    const  filteredCapital=filteredCountry.map((el)=>el.capital)

    console.log(weather);


    useEffect(()=> {
       axios.get( 'https://restcountries.eu/rest/v2/all')
                          .then((response)=>setCountries(response.data))
                           },[])

    useEffect(()=> {
        if(filteredCapital[0]!==undefined) {
           axios.get( `http://api.weatherapi.com/v1/current.json?key=${process.env.REACT_APP_API_KEY}&q=${filteredCapital[0]}`)
            .then(response=>setWeather(response.data))
               }  },[searchcountry]) 


    const handleSearch=(e)=>setSearch(e.target.value)

    const handleShow =(e)=>setSearch(e.target.value)

    return(<div>

        find countries:<input type='text' value={searchcountry} onChange={handleSearch}/>
        {
           ( filteredCountry.length>10 ? 
             <p>Too many matches,specify another filter</p>:
            ( filteredCountry.length===1 ? 
              filteredCountry.map((el)=>
                 <div key={el.name}>
                  <h1 >{el.name}</h1>
                  <p>capital: {el.capital}</p>
                  <p>population: {el.population}</p>
                  <h2>Languages</h2>
                  { el.languages.map((language)=>
                  <li key={language.name}>{language.name}</li>) }
                  <img src={el.flag} alt='flag' width='100'/>
                  <h1>Weather in {weather.location.name}</h1>
                  <p>Temperatures: {weather.current.temp_c}</p>
                  <img src={weather.current.condition.icon } alt="weather "/>
                  <p><b>wind:</b>{weather.current.wind_mph}mph direction {weather.current.wind_dir}</p>
                  </div>) :
              filteredCountry.map((el)=>
                 <div key={el.name}>
                  {el.name} 
                  <button onClick={handleShow} value={el.name}>show</button>
                  </div>) 
              ))
        }




    </div>)
}

export default App

