import React, { useState } from 'react'

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas',number:98456709 }
  ]) 
const [ newName, setNewName ] = useState('')

const [ newNumber, setNewNumber ] = useState('')

const [ filteredContact, setFilteredContact ] = useState('')

const handlePersonInput=(event)=>setNewName(event.target.value)

const handlePersonNumber=(event)=>setNewNumber(event.target.value)

const handleFiltered=(event)=>{
   console.log(  persons.filter((el)=>el.name.includes(event.target.value)) );
}

//setFilteredContact(event.target.value)

const addPerson=(event)=>{
                event.preventDefault()

                if( persons.some((el)=>el.name.toLowerCase()===newName.trim().toLowerCase()) ) {
                    alert(`${newName} is already added to phonebook`)
                } 

               else{
                const newperson={name:newName,number:newNumber}
                setPersons(persons.concat(newperson))
                setNewName('') 
                setNewNumber('')
               }}

 return (
            <div>
            <h2>Phonebook</h2>
              Filter shown with <input value={filteredContact} onChange={handleFiltered} />
            <h1>Add new contact</h1>
            <form onSubmit={addPerson}>
                <div>
                name: <input   value={newName} onChange={handlePersonInput} />
                </div>
                <div>
                Number: <input   value={newNumber} onChange={handlePersonNumber} />
                </div>
                <div>
                <button type="submit"  >add</button>
                </div>
            </form>
            <h2>Numbers</h2>
            {  persons.map( (element)=><p key={element.name}>{element.name} {element.number}</p>) }
            </div>
        )
}

export default App
