import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const Header=(props)=> <h1>{props.course} </h1>

const Content=(props)=> <div> <Part part={props.part1} exercises={props.exercise1}/>
                              <Part part={props.part2} exercises={props.exercise2}/>
                              <Part part={props.part3} exercises={props.exercise3}/>
                        </div>



const Total=(props)=>  <p> Total no of exercise is  {props.exercise1 + props.exercise2 + props.exercise3}</p> 

const Part=(props)=><p>{props.part}{props.exercises}</p>

const App=()=>
              {
                const course = 'Half Stack application development'
                 const part1 = 'Fundamentals of React'
                const exercises1 = 10
                const part2 = 'Using props to pass data'
                const exercises2 = 7
                const part3 = 'State of a component'
                const exercises3 = 14

                return(<div>

                        < Header course={course}/>
                        < Content part1={part1} exercises1={exercises1}
                                  part2={part2} exercise2={exercises2} 
                                  part3={part3} exercise3={exercises3}
                        />
                        < Total  exercise1={exercises1} exercise2={exercises2} exercise3={exercises3}/> 

                     </div>)
              }

ReactDOM.render(
  <App />,
   document.getElementById('root')
);
