      const App=()=>{
       const [click,setClick]=useState({ left:0, right:0 })

       const handleLeft=()=>{
                                  const newClick={left:click.left+1,
                                  right:click.right  }

      setClick(newClick)
       }

       const handleRight=()=>{
                              const newClick={left:click.left,
                              right:click.right+1  }

      setClick(newClick)
      }


         return(<div>
              {click.left}
              <button onClick={handleLeft}>left</button>
              <button onClick={handleRight}>right</button>
              {click.right}
             </div>)

      }


ReactDOM.render(
  <App />,
   document.getElementById('root')
);
