import axios from 'axios'
const baseurl='http://localhost:3001/persons'

const getAll=()=>{
    return(axios.get(baseurl))
}

const create=(newObject)=>{
    return(axios.post('http://localhost:3001/persons',newObject))
}


export default {
      getAll,create}
