import { useState, useEffect } from 'react'
import axios from 'axios'

const App=()=>{

    const[countries,setCountries]=useState([])

    const[searchcountry,setSearch]=useState('')

     const filteredCountry=countries.filter(
                     (country)=>country.name.toUpperCase().includes(searchcountry.toUpperCase()) ) 

    useEffect(()=> {
       axios.get( 'https://restcountries.eu/rest/v2/all')
                          .then((response)=>setCountries(response.data) )


    },[])

    useEffect(()=> {
    axios.get(
       `http://api.weatherstack.com/current?access_key=${process.env.REACT_APP_API_KEY}&query=${searchcountry}`)
       .then(response=>console.log(response.data.current))


   })

    const handleSearch=(e)=>setSearch(e.target.value)

    const handleShow =(e)=>setSearch(e.target.value)


    return(<div>

        find countries:<input type='text' value={searchcountry} onChange={handleSearch}/>
        {
           ( filteredCountry.length>10 ? 
             <p>Too many matches,specify another filter</p>:
            ( filteredCountry.length===1 ? 
              filteredCountry.map((el)=>
                 <div key={el.name}>
                  <h1 >{el.name}</h1>
                  <p>capital: {el.capital}</p>
                  <p>population: {el.population}</p>
                  <h2>Languages</h2>
                  { el.languages.map((language)=>
                  <li key={language.name}>{language.name}</li>) }
                  <img src={el.flag} alt='flag' width='100'/>
                  </div>) :
              filteredCountry.map((el)=>
                 <div key={el.name}>
                  {el.name} 
                  <button onClick={handleShow} value={el.name}>show</button>
                  </div>) 
              ))
        }




    </div>)
}

export default App
